import { useDispatch, useSelector } from "react-redux";
import Box from "@mui/material/Box";
import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import Typography from "@mui/material/Typography";
import { editItemActions } from "../store/todo-slice";

function ToDoList() {
  const items = useSelector((state) => state.toDo.items);
  const dispatch = useDispatch();
  const removeItemHandler = (e, key) => {
    e.stopPropagation();
    dispatch(editItemActions.removeItem(key));
  };

  return items.map((item) => (
    <Box
      onClick={removeItemHandler}
      sx={{ minWidth: 275, maxWidth: 300, alignItems: "center", mx: "auto" }}
      key={item}
    >
      <Card variant="outlined">
        <CardContent sx={{ textAlign: "center", backgroundColor: "#F9BE61" }}>
          <Typography sx={{ fontSize: 20 }} color="white" gutterBottom>
            {item}
          </Typography>
        </CardContent>
      </Card>
    </Box>
  ));

  //   return items.map((item) => <p>{item}</p>);
}

export default ToDoList;
